import React from "react";
import AddBurgerForm from "./AddBugerForm";
import EditBurgerForm from "./EditBurgerForm";

class MenuAdmin extends React.Component {
  state = { burgers: this.props.burgers };

  render() {
    return (
      <div className="menu-admin">
        <h2>Menu control</h2>
        {Object.keys(this.state.burgers).map((key) => {
          return (
            <EditBurgerForm
              key={key}
              index={key}
              burger={this.state.burgers[key]}
              updateBurger={this.state.updateBurger}
              deleteBurger={this.state.deleteBurger}
            />
          );
        })}
        <AddBurgerForm addBurger={this.state.addBurger} />
        <button onClick={this.state.loadSimpleBurgers}>Loud Burgers</button>
      </div>
    );
  }
}

export default MenuAdmin;
