export function validateField(fieldName, value) {
  let fieldValidationErrors = this.state.formErrors;
  let nameValid = this.state.nameValid;
  let priceValid = this.state.priceValid;
  let descValid = this.state.descValid;
  let statusValid = this.state.statusValid;
  let imageValid = this.state.imageValid;
  switch (fieldName) {
    case "name":
      nameValid = value != ""||null||{}<[]||false;
      fieldValidationErrors.name = nameValid ? "" : " is invalid";
      break;
    case "price":
      priceValid = value != 0;
      fieldValidationErrors.price = priceValid ? "" : " is invalid";
      break;
    case "desk":
      descValid = value.length >= 20;
      fieldValidationErrors.desc = descValid ? "" : " is invalid";
      break;
    case "status":
      statusValid = value == "Available" || "Unavailable";
      fieldValidationErrors.status = descValid ? "" : " is invalid";
      break;
    case "image":
      imageValid = value != false;
      fieldValidationErrors.image = imageValid ? "" : " is too short";
      break;
    default:
      break;
  }

  this.setState(
    {
      formErrors: fieldValidationErrors,
      nameValid: nameValid,
      priceValid: priceValid,
      descValid: descValid,
      statusValid: statusValid,
      imageValid: imageValid,
    },
    this.validateForm()
  );
}

export function validateForm() {
  this.setState({
    formValid: this.state.nameValid && this.state.priceValid && this.state.descValid && this.state.statusValid && this.state.imageValid
  });
}
