import React, { useState } from 'react';
import PropTypes from 'prop-types';
import restaurants from '../sample-restaurants';

const Landing = props => {
  const [display, toggleDisplay] = useState(false);
  const [title, setTitle] = useState('');
  const [url, setUrl] = useState('');

  const displayList = () => {
    toggleDisplay(!display);
  };

  const getTitle = restaurant => {
    const { title, url } = restaurant;
    setTitle(title);
    setUrl(url);
    toggleDisplay(false);
  };

  const gotToRestaurant = () => {
    props.history.push(`/restaurant/${url}`);
  };

  return (
    <div className='restaurant_select'>
      <div className='restaurant_select_top'>
        <div
          onClick={displayList}
          className='restaurant_select_top-header font-effect-outline'
        >
          {title ? title : 'select a restaurant'}
        </div>
        <div className='arrow_picker'>
          <div className='arrow_picker-up'></div>
          <div className='arrow_picker-down'></div>
        </div>
      </div>

      {display ? (
        <div className='restaurant_select_bottom'>
          <ul>
            {restaurants.map(restaurant => {
              return (
                <li onClick={() => getTitle(restaurant)} key={restaurant.id}>
                  {restaurant.title}
                </li>
              );
            })}
          </ul>
        </div>
      ) : null}

      {title && !display ? (
        <button onClick={gotToRestaurant}>go to restaurant</button>
      ) : null}
    </div>
  );
};

Landing.propTypes = {
  history: PropTypes.object
};

export default Landing;
